DROP TABLE IF EXISTS countries;

CREATE TABLE countries (
    id SMALLINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    iso2 CHAR(2) NOT NULL UNIQUE, -- 2-letter ISO country code
    iso3 CHAR(3) NOT NULL UNIQUE, -- 3-letter ISO country code
    currency_code CHAR(5) NOT NULL, -- ISO 4217 currency code (e.g., USD, EUR, GBP)
    currency_name VARCHAR(50) NOT NULL, -- Full currency name (e.g., US Dollar, Euro, British Pound)
    currency_symbol VARCHAR(5) NOT NULL, -- Currency symbol (e.g., $, €, £)
    phone_code VARCHAR(10) NOT NULL, -- Country calling code (e.g., +1, +44)
    phone_format VARCHAR(20) NOT NULL, -- Example: (###) ###-####
    continent TEXT NOT NULL CHECK (continent IN ('Africa', 'Asia', 'Europe', 'North America', 'Oceania', 'South America', 'Antarctica')),
    region VARCHAR(50) NOT NULL, -- Example: Western Europe, Sub-Saharan Africa
    subregion VARCHAR(50) NOT NULL, -- Example: Caribbean, Southern Africa
    latitude DECIMAL(8,6) NOT NULL, -- Precision for geolocation
    longitude DECIMAL(9,6) NOT NULL,
    capital VARCHAR(100) NOT NULL, -- Capital city
    tld VARCHAR(10) NOT NULL, -- Top-Level Domain (.us, .uk, .de)
    flag_emoji TEXT NOT NULL, -- Unicode flag emoji
    created_at TIMESTAMPTZ DEFAULT NOW(),
    updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Keep existing indexes
CREATE INDEX idx_countries_name ON countries (name);
CREATE INDEX idx_countries_continent ON countries (continent);
CREATE INDEX idx_countries_region ON countries (region);
CREATE INDEX idx_countries_phone_prefix ON countries (phone_code);
CREATE INDEX idx_countries_latlong ON countries USING GIST (point(latitude, longitude));
CREATE INDEX idx_countries_currency ON countries (currency_code);

